export interface Addon {
  static: boolean;
  limit: number | boolean | string[];
  id: null | string;
}

export interface Addons {
  [key: string]: Addon;
}

interface PlanUnitPeriod {
  unit_amount_decimal?: number;
  id: number | null;
}

export interface Subscription {
  orgId: string;
  userId?: string;
  tag: string;
  unit?: {
    subscriptionId: string;
    annual: boolean;
    status: string;
    current_period_end: string;
    cancel_at: string;
    item: PlanUnitPeriod;
  };
  tier: {
    subscriptionId?: string;
    status?: string;
    current_period_end?: string;
    cancel_at?: string;
    addons: Addons;
  };
}
